partials.blocks_attributes_core-media-text:
{{#json key="attributes" type="object"}}
  {{#if attrs.className}}{{{json key="className" value=attrs.className}}},{{/if}}
  {{#if attrs.align}}{{{json key="align" value=attrs.align}}},{{/if}}
  {{#if attrs.mediaId}}{{{json key="mediaId" value=attrs.mediaId}}},{{/if}}
