partials.blocks_attributes_core-spacer:
{{#json key="attributes" type="object"}}
  {{#if attrs.className}}{{{json key="className" value=attrs.className}}},{{/if}}
  {{#if (itemAt (itemAt (match (trim innerHTML) "(?:height:)([0-9]+)(?:px)") 0) 1)}}
