{{/json}},

partials.blocks_contents_core-gallery:
{{#json key="contents" type="array"}}
  {{#match innerHTML "(?:<li.*?>)(.*?)(?:<\/li>)"}}
    {{#json}}
      {{#if (itemAt (itemAt (match match.[1] '(?:src=")(.*?)(?:")') 0) 1)}}
        {{{json key="src" value=(get this "@input")}}},
      {{/if}}
      {{#if (itemAt (itemAt (match match.[1] '(?:href=")(.*?)(?:".*?><img)') 0) 1)}}
        {{{json key="href" value=(get this "@input")}}},
      {{/if}}
      {{#if (itemAt (itemAt (match match.[1] '(?:alt=")(.*?)(?:")') 0) 1)}}
        {{{json key="alt" value=(get this "@input")}}},
      {{/if}}
      {{#if (itemAt (itemAt (match match.[1] '(?:data-id=")(.*?)(?:")') 0) 1)}}
        {{{json key="id" value=(get this "@input")}}},
      {{/if}}
      {{#if (itemAt (itemAt (match match.[1] '(?:<figcaption.*?>)(.*?)(?:<\/figcaption)') 0) 1)}}
        {{{json key="caption" value=(get this "@input")}}},
      {{/if}}
    {{/json}},
  {{/match}}
{{/json}}

partials.blocks_contents_core-pullquote:
{{#json key="contents" type="object"}}
  {{#match innerHTML "(?:<blockquote[\s\S]*?>)([\s\S]*?)((?:<cite[\s\S]*?>)([\s\S]*?)(?:<\/cite>))?(?:<\/blockquote>)"}}
    {{#if match.[1]}}{{{json key="quote" value=(get this "@input")}}},{{/if}}
    {{#if match.[3]}}{{{json key="citation" value=(get this "@input")}}},{{/if}}
  {{/match}}
{{/json}},

